customWidget.javascript:
let counter = 0
let message = ''

getValue('message',(tempVar) => {
      
      tempVar = tempVar ? tempVar : 'Warning'
      message = tempVar;

});

getValue('call_alert',(tempVar) => {
      
      global_values = tempVar
      if (tempVar > counter) {
          counter = tempVar;
          alert (message);
      }
      
});

